import { createRouter, createWebHashHistory } from 'vue-router';
import PaginaInicio from '@/components/PaginaInicio.vue';
import TablaClientes from '@/components/TablaClientes.vue';
import TablaContacto from '@/components/TablaContacto.vue';
import TablaEmpleo from '@/components/TablaEmpleo.vue';
import NotFound from '@/components/NotFound.vue';

const routes = [
  {
    path: '/',
    name: 'inicio',
    component: PaginaInicio
  },
  {
    path: '/clientes',
    name: 'clientes',
    component: TablaClientes
  },
  {
    path: '/contacto',
    name: 'contacto',
    component: TablaContacto
  },
  {
    path: '/empleo',
    name: 'empleo',
    component: TablaEmpleo
  },
  // Ruta para capturar cualquier coincidencia incorrecta
  {
    path: '/:pathMatch(.*)*',
    name: 'NotFound',
    component: NotFound
  }
];

const router = createRouter({
  history: createWebHashHistory(),
  routes
});

// Redirige cualquier intento de acceso a una ruta no válida con hash al 404
router.beforeEach((to, from, next) => {
  if (to.hash && !routes.some(route => route.path === to.path)) {
    next({ name: 'NotFound' });
  } else {
    next();
  }
});

export default router;
